phasor_spacetime = [calculatePhasor(omega_spacetime * t_spacetime, k_spacetime * x_spacetime, magnitude_spacetime, phase_spacetime)]

{
  const phase_plot = Plot.plot({
    x: {
      label: "Real",
      domain: [-2, 2],
    },
    y: {
      label: "Imaginary",
      domain: [-2, 2],
    },
    ratio: 1,
    width: 400,
